properties.ejemplo_mapa_mental_para_guiarte:
mindmap
  root((Idea Principal))
    Rama1(Tema Uno)
      SubIdea1_1{{Detalle A}}
      SubIdea1_2{{Detalle B}}
    Rama2(Tema Dos)
    Rama3(Tema Tres)
      SubIdea3_1{{Detalle C}}

properties.ejemplo_diagrama_de_flujo_para_guiarte:
graph TD
    A(Inicio ) --> B[Preparar Café ];
    B --> C{¿Está Dulce? };
    C --  Sí --> D[Disfrutar ];
    C --  No --> E[Añadir Azúcar ];
    E --> D;
    D --> F(Fin );

    style A fill:#caffbf,stroke:#3f5b32,stroke-width:2px
    style F fill:#caffbf,stroke:#3f5b32,stroke-width:2px
    style C fill:#ffdac1,stroke:#a0522d
    style D fill:#bde0fe,stroke:#0077b6,stroke-width:3px
    style E fill:#fff3b0,stroke:#e6a700
